/* Shorthand Operator */
let number1 = 5;

// number1 = number1 + 5;
console.log("Addition", number1 += 5);
// number1 = number1 - 5;
console.log("Subtraction", number1 -= 5);
// number1 = number1 * 5;
console.log("Multiplication", number1 *= 5 );
// number1 = number1 / 5;
console.log("Division", number1 /= 5);
// number1 = number1 % 5;
console.log("Modulus", number1 %= 5);
// number1 = number1 ** 5;
console.log("Exponent", number1 **= 2);



